import React, { useState, useEffect } from 'react';
import { useLazyGetSummaryQuery } from '../services/article';
import '../static/content.css';
// import mainlogo from '../Content (1).png';
import mainlogo from '../assets/Name.png';
import copy from '../Copy.png';

const hexhead = {
  color: '#848484'
};

export default function Input() {

  const [article, setArticle] = useState({
    url: "",
    summary: "",
  });
  const [allArticle, setallArticle] = useState([]);
  const [allUpdatedArticle, setAllUpdatedArticle] = useState([]);


  const [getSummary, { error, isFetching }] = useLazyGetSummaryQuery();
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setArticle({ ...article, url: e.target.value });
  }

  // const copySummary = () => {
  //   const summaryText = article?.summary;
  //   if (summaryText) {
  //     const textArea = document.createElement('textarea');
  //     textArea.value = summaryText;
  //     document.body.appendChild(textArea);
  //     textArea.select();
  //     document.execCommand('copy');
  //     document.body.removeChild(textArea);
  //   }
  // };

  const copySummary = () => {
    var copyText = article?.summary;
    navigator.clipboard.writeText(copyText.value);
  };

  //  const handleUrlClick = (index) => {
  //  setArticle(allArticle[index]?.summary);
  //  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    const { data } = await getSummary({ articleUrl: article.url });
    if (data?.summary) {
      const newArticle = { ...article, summary: data.summary };
      const updatedAllArticle = [newArticle, ...allArticle.slice(0, 4)];
      setIsLoading(false);
      setArticle(newArticle);
      setallArticle(updatedAllArticle);
    }
    // 
  }

  useEffect(() => {
    localStorage.setItem('items', JSON.stringify(allArticle));
  }, [allArticle]);

  useEffect(() => {
    const allArticleShow = localStorage.getItem('items');
    console.log(allArticleShow);
    if (allArticleShow) {
     setAllUpdatedArticle(JSON.parse(allArticleShow));
      console.log(allUpdatedArticle)
    }
  }, []);


  return (
    <div className="dev">
      <div>
        <center>
      <img height={105} width={350} src={mainlogo}/>
      <h5 className='heading'>Summarize any website, anywhere.</h5>
      </center>
      </div>
      <br/>
      <div>
        <form type="post" onSubmit={handleSubmit}>

          <div className='ept'>
            <input className="inp" type="url" placeholder='Enter the URL: ' onChange={handleChange} style={{width: '350px', height: '40px'}}></input>
            <button className="butn" type="submit" alt="Copy">Summarize</button>
          </div>

        </form>
        <br/>
        <br/>

      </div>
      <div className="storedUrl">
        <center>
          <ul>
            {allUpdatedArticle.map((item, index) => (
              <div>

                <div className="arri" style={{zIndex: 5}}>
                  <li key={index}>{item.url}</li>
                  <button onClick={() => {navigator.clipboard.writeText(allArticle[index]?.summary)}}><img src={copy}/></button>
                </div>   
              </div>

            ))}
          </ul>
        </center>
        {isLoading ? 
          <div className="loader-container"> // style={{zIndex: 10}}
            <div className="loader"></div>
          </div> :
          <div className='summary'>
            <div className='bjing'>
              <h4>Summary</h4>
              <button onClick={() => {navigator.clipboard.writeText(article?.summary)}}><img src={copy}/></button>
            </div>
            <br/>
            <br/>
            <p>{article?.summary}</p>
          </div>
         }

      </div>
    </div>
  )
}
